ecl:shell-command.ecl:command:
(cd /var/log;for f in boot.log* cron* dmesg httpd/* ksyms* maillog* messages* pgsql rpmpkgs*
                                samba/* sa/* scrollkeeper.log secure* spooler*; do find $f -maxdepth 0 -perm /g=w -ls 2>/dev/null;
                                done)

xccdf:Rule.xccdf:fixtext:
Action:cd /etc if [ "`grep -l sulogin inittab`" = "" ]; then awk '{ print };
                    /^id:[0123456sS]:initdefault:/ \ { print "~:S:wait:/sbin/sulogin" }' \ inittab > inittab.tmp mv -f inittab.tmp
                    inittab chown root:root inittab chmod 644 inittab fi

